INSERT INTO `tp_wc_admin_notes` (`note_id`, `name`, `type`, `locale`, `title`, `content`, `icon`, `content_data`, `status`, `source`, `date_created`, `date_reminder`, `is_snoozable`, `layout`, `image`, `is_deleted`) VALUES
(1, 'wc-admin-onboarding-email-marketing', 'info', 'en_US', 'Astuces, mises à jour des produits et inspiration', 'Nous sommes là pour vous, obtenir des astuces, mises à jour de produits et inspirations directement dans votre boîte e-mail', 'mail', '{}', 'unactioned', 'woocommerce-admin', '2020-06-09 11:59:40', NULL, 0, '', NULL, 0),
(2, 'wc-admin-marketing-intro', 'info', 'en_US', 'Connectez-vous à votre audience', 'Faites croître votre base clients et augmentez vos ventes avec les outils marketing créés pour WooCommerce.', 'speaker', '{}', 'unactioned', 'woocommerce-admin', '2020-06-09 11:59:40', NULL, 0, '', NULL, 0),
(3, 'wc-admin-wc-helper-connection', 'info', 'en_US', 'Se connecter à WooCommerce.com', 'Connectez-vous pour obtenir des notifications et des mises à jour importantes sur les produits.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-06-09 11:59:40', NULL, 0, '', NULL, 0),
(4, 'wc-admin-onboarding-profiler-reminder', 'update', 'en_US', 'Bienvenue dans WooCommerce ! Configurer votre boutique et commencer à vendre', 'Notre objectif est de vous aider tout au long des étapes les plus importantes du lancement de votre boutique.', 'info', '{}', 'actioned', 'woocommerce-admin', '2020-06-09 07:59:48', NULL, 0, '', NULL, 0),
(5, 'wc-admin-mobile-app', 'info', 'en_US', 'Installer l\'application mobile Woo', 'Installez l\'application mobile WooCommerce pour gérer les commandes, recevoir des notifications de vente et afficher les statistiques principales où que vous soyez.', 'phone', '{}', 'unactioned', 'woocommerce-admin', '2020-06-25 03:26:43', NULL, 0, 'plain', NULL, 0),
(6, 'wc-admin-usage-tracking-opt-in', 'info', 'en_US', 'Aider WooCommerce à améliorer ses services grâce au suivi de l\'utilisation', 'Récupérer les données d\'utilisation nous permet d\'améliorer WooCommerce. Votre boutique sera prise en compte lors de l\'évaluation des nouvelles fonctionnalités et de la qualité d\'une mise à jour, ou pour déterminer l\'intérêt d\'une amélioration. Vous pouvez toujours consulter les <a href=\"http://localhost:8888/wordpress/wp-admin/admin.php?page=wc-settings&#038;tab=advanced&#038;section=woocommerce_com\" target=\"_blank\">Paramètres</a> et choisir d\'interrompre le partage des données. <a href=\"https://woocommerce.com/usage-tracking\" target=\"_blank\">En savoir plus</a> sur les données que nous collectons.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-06-25 07:26:43', NULL, 0, '', NULL, 0),
(7, 'wc-admin-store-notice-giving-feedback-2', 'info', 'en_US', 'Donner des commentaires', 'Maintenant que vous nous avez choisis comme partenaire, notre objectif est de nous assurer que nous vous fournissons les bons outils pour répondre à vos besoins. Nous sommes impatients de recevoir vos commentaires sur votre expérience de conception de la boutique pour que nous puissions l\'améliorer à l\'avenir.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-07-09 12:01:34', NULL, 0, 'plain', '', 0),
(8, 'wc-admin-need-some-inspiration', 'info', 'en_US', 'Besoin d\'inspiration ?', 'Découvrez quelques-unes de nos histoires de clients préférées d\'entrepreneurs, d\'agences et de développeurs de notre communauté mondiale.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-07-09 12:01:34', NULL, 0, 'plain', '', 0),
(9, 'wc-admin-learn-more-about-product-settings', 'info', 'en_US', 'En savoir plus sur les paramètres de produit', 'Dans cette vidéo, vous trouverez des informations sur la configuration des paramètres de produit, comme leur mode d\'affichage, la modification des options d\'inventaire, etc.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-07-09 12:01:34', NULL, 0, 'plain', '', 0),
(10, 'wc-admin-choose-niche', 'info', 'en_US', 'Comment choisir une niche pour votre boutique en ligne', 'Votre niche définit les produits et les services que vous développez. Elle dirige votre marketing. Elle focalise votre attention sur les problèmes spécifiques que rencontrent vos clients. Elle vous différencie de la concurrence. Découvrez les cinq principes directeurs pour définir votre niche.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-07-09 12:01:34', NULL, 0, 'plain', '', 0);
